import React, { Component } from "react";

class GoogleAuth extends Component {
  state = {
    isSignedIn: null
  };
  componentDidMount() {
    window.gapi.load("client:auth2", () => {
      window.gapi.client
        .init({
          clientId:
            "514680687151-9q89f01sbgm0lrsrur9qe3efqo5anj0k.apps.googleusercontent.com",
          scope: "email"
        })
        .then(() => {
          this.auth = window.gapi.auth2.getAuthInstance();
          this.setState({
            isSignedIn: this.auth.isSignedIn.get()
          });
          this.auth.isSignedIn.listen(this.onAuthChange); //whenever user's status changes call onAuthChange
        });
    });
  }

  renderAuthButton = () => {
    if (this.state.isSignedIn === null) return <div>dont know</div>;
    else if (this.state.isSignedIn) return <button>Log in!</button>;
    else return <button>Log Out!</button>;
  };

  onAuthChange = () => {
    this.setState({
      isSignedIn: this.auth.isSignedIn.get()
    });
  };

  render() {
    return <div>{this.renderAuthButton()}</div>;
  }
}

export default GoogleAuth;
