const INITIAL_STATE = {
  isSignedIn: null
};

export const authReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case "SIGN_IN":
      return { ...state, isSignedIn: true };
    case "SIGN_IN":
      return { ...state, isSignedIn: false };
    default:
      return state;
  }
};
